(window["webpackJsonp"]=window["webpackJsonp"]||[]).push([["chunk-9d2f2ce4"],{8404:function(e,t,c){"use strict";c.r(t);var n=c("7a23");function o(e,t,c,o,r,a){return Object(n["openBlock"])(),Object(n["createElementBlock"])("div",null,[(Object(n["openBlock"])(!0),Object(n["createElementBlock"])(n["Fragment"],null,Object(n["renderList"])(r.articles,(function(e){return Object(n["openBlock"])(),Object(n["createElementBlock"])("div",{key:e,class:"school-container"},[Object(n["createElementVNode"])("p",null,Object(n["toDisplayString"])(e[0]),1)])})),128))])}var r={data:function(){return{articles:[["Муниципальное автономное общеобразовательное учреждение «Средняя общеобразовательная школа №61» муниципального образования города Чебоксары – столицы Чувашской Республики (далее школа №61) самая большая школа республики, в которой в настоящее время в 69 классах обучается 1929 учащихся."],["Главная цель школы – создание условий для формирования творческой саморазвивающейся личности, способной к успешной адаптации и самореализации в изменяющемся мире."],["Структура и содержание деятельности школы позволяет каждому учащемуся осуществлять выбор в области обучения и во внеурочной деятельности в соответствии со своими интересами и возможностями и быть успешным в той или иной сфере школьной жизни."],["Многолетний опыт работы школы по программам развивающего обучения Д.Б. Эльконина-В.В. Давыдова позволил педагогическому коллективу освоить  профессиональные компетенции, которые полностью соответствуют требованиям ФГОС и позволяют осуществлять качественное образование. Результаты ГИА и ЕГЭ стабильно выше городских, республиканских и российских. Школа более 16 лет является лидером среди общеобразовательных школ города по результатам муниципального и регионального этапов Всероссийской олимпиады школьников."],["Более 60% учащихся школы занимаются живописью, музыкой, вокалом и хореографией на базе школы ХКО. Они принимают активное участие в музык альных, художественных, хореографических мероприятиях и являются лауреатами и дипломатами различных творческих конкурсов, от районных и городских, до международных."],["Спортивная база школы включает два бассейна, три спортивных зала, стадион и спортивные площадки. Спортсмены школы занимают призовые места в районных, городских, республиканских и российских соревнованиях по различным видам спорта. На базе спортивно-оздоровительного комплекса воспитаны 15 кандидатов в мастера спорта по плаванию и 8 мастеров спорта, так футбольная команда школы заняла I место в ПФО и II место в России."],["Возможность выбора видов внеурочной деятельности по интересам способствуют успешной социализации обучающихся, формируют уверенность в себе и  позитивное отношение к жизни."],["В школе создана воспитательная система, которая основывается на технологиях коллективных творческих дел и коллективной организаторской деятельности. Учащимся школы предоставляется возможность реализовать свои лидерские качества, развивать коммуникативные навыки, творческие способности в общественной работе, в массовых мероприятиях, которые проводятся не только в школе, но и в микрорайоне."],['Традиции формируют дух школы, определяют ее лицо, являются объединяющим началом для детей и педагогов. Такие коллективные творческие дела, как «День знаний», «Примите наши поздравления», «Мы начинаем КВН», фестиваль "Творческая весна», спортивно-патриотическая игра «Зарница», праздник окончания учебного года – являются для учащихся школой жизни. Фестиваль «Творческая весна» традиционно проводится в ДК «Салют», в зале на 700 мест, при полном аншлаге,  до 400 учащихся участвуют в фестивале, представляя свое творчество непосредственно на сцене.'],['С 2004 года школа участвует в международном проекте "Ассоциированные школы ЮНЕСКО".'],['В 2015 году мы стали партнерской школой НИУ "Высшая школа экономики".'],["Учебно-воспитательный процесс осуществляют 116 педагогических работников, 82% из них имеют первую и высшую квалификационную категории. Педагоги школы не раз становились победителями городского и финалистами республиканского конкурса профессионального мастерства «Учитель года». Восемь педагогов школы являются лауреатами премии Президента РФ в рамках национального проекта «Образование», пять педагогов удостоены премии Главы Чувашской Республики."],["Школа дважды вошла в топ-500 лучших школ России и является единственной общеобразовательной школой Чувашской Республики, вошедшей в число 500 лучших школ страны. В 2014, 2015, 2016 и 2017 годах школа была включена в национальный реестр РФ «Ведущие образовательные учреждения»."],["Победитель Всероссийского конкурса «100 ЛУЧШИХ ПРЕДПРИЯТИЙ И ОРГАНИЗАЦИЙ РОССИИ-2018» в номинации «ЛУЧШАЯ ШКОЛА РОССИИ»."],["Лауреат Всероссийского конкурса «ОБРАЗОВАТЕЛЬНАЯ ОРГАНИЗАЦИЯ XXI ВЕКА. ЛИГА ЛИДЕРОВ - 2018» в номинации «ЛИДЕР В ОБЛАСТИ ВНЕДРЕНИЯ ИННОВАЦИОННЫХ ТЕХНОЛОГИЙ»"]]}}},a=(c("8ba3"),c("6b0d")),l=c.n(a);const i=l()(r,[["render",o],["__scopeId","data-v-51153892"]]);t["default"]=i},"8ba3":function(e,t,c){"use strict";c("fe13")},fe13:function(e,t,c){}}]);
